      }}
    >
      <div className="App">
      <label>Text</label>
        <input
          type="text"
          required
          value={values.text}
          placeholder="Enter the text..."
          onChange={handleTextInput}
        />
        <label>Datetime</label>
        <input
          type="datetime-local"
          value={values.datetime}
          onChange={handleDateTimeInput}
        />
        <button>Add</button>
      </div>
    </form>
  );
};
