// import logo from "./logo.svg";
import "./App.css";
import { v4 as uuid } from "uuid";
import React, { useState } from "react";

function App() {
  const unique_id = uuid();
  const small_id = unique_id.slice(0, 8);
  const [values, setValues] = useState({
    text: "",
    datetime: "",
  });

  const [state, setState] = useState([]);
  // console.log(state, "ckst");

  const handleTextInput = (e) => {
    setValues({ ...values, text: e.target.value });
  };

  const handleDateTimeInput = (e) => {
    setValues({ ...values, datetime: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const text = values.text;
    const datetime = values.datetime;
    const data = {
      id: small_id,
      text,
      datetime,
    };
    addData(data);
  };

  const addData = (data) => {
    // return state.push(data)
    setState([...state, data]);
    // console.log(result)
  };
  console.log(state)
  return (
    <form
      onSubmit={(e) => {
        handleSubmit(e);
      }}
    >
      <div className="App">
        <input
          type="text"
          required
          value={values.text}
          placeholder="Enter the text..."
          onChange={handleTextInput}
        />
        <input
          type="datetime-local"
          value={values.datetime}
          onChange={handleDateTimeInput}
        />
        <button>Add</button>
        <button
          onClick={() =>
            setValues({ text: values.text, datetime: values.datetime })
          }
        >
          Edit
        </button>
        <button>Delete</button>
      </div>
      {state.length > 0 &&
        state.map((item) => {
          return (
            <div>
              {" "}
              {/* <h1>{item.id}</h1>
              <h1>{item.text}</h1>
              <h1>{item.datetime}</h1> */}
              <input value={item.text} type="text" onChange={handleTextInput} />
              <button onClick={(e) => console.log(state)}>Edit</button>
              <button>Delete</button>
            </div>
          );
        })}
    </form>
  );
}

export default App;
