// import logo from "./logo.svg";
import "./App.css";
import React, { useState } from "react";

function App() {
  const [values, setValues] = useState({
    text: "",
    datetime: "",
  });

  const [state, setState] = useState([]);
  console.log(state, "ckst");

  const handleTextInput = (e) => {
    setValues({ ...values, text: e.target.value });
  };

  const handleDateTimeInput = (e) => {
    setValues({ ...values, datetime: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const text = values.text;
    const datetime = values.datetime;
    const data = {
      id: new Date(),
      text,
      datetime,
    };
    addData(data);
  };

  const addData = (data) => {
    // return state.push(data)
    setState(...state, data);
    // console.log(result)
  };
  return (
    <form
      onSubmit={(e) => {
        handleSubmit(e);
      }}
    >
      <div className="App">
        <input
          type="text"
          required
          value={values.text}
          placeholder="Enter the text..."
          onChange={handleTextInput}
        />
        <input
          type="datetime-local"
          value={values.datetime}
          onChange={handleDateTimeInput}
        />
        <button>Add</button>
        <button
          onClick={() =>
            setValues({ text: values.text, datetime: values.datetime })
          }
        >
          Edit
        </button>
        <button>Delete</button>
      </div>
      {state.length > 0 && state.id}
    </form>
  );
}

export default App;
